CREATE OR REPLACE PROCEDURE public.kit_statistics_pcd() AUTHID DEFINER
    IS $__sys__$ 
BEGIN

execute immediate 'truncate table KIT_STATISTICS';

execute immediate 'truncate table pre_kit_statistics';


commit;

insert into pre_kit_statistics
   select /*+parallel*/ (case when c.ref_device_id is null then upper(c.mac_address)  else c.REF_DEVICE_ID end) as device, n.id,
   max(duration) as max_, min(duration) as min_ , avg(duration) as avg_
   from client_activity_log c
   left join enrollment_ref e on (case when c.ref_device_id is null then upper(c.mac_address)  else c.REF_DEVICE_ID end) =
   (case when e.device_id is null then upper(e.mac_address) else e.device_id end)
   left join node n on n.enrollment_ref = e.id
   where c.ACTIVITY <> 'END_TO_END_CAPTURE'
   and duration > 0
   group by n.id,(case when c.ref_device_id is null then upper(c.mac_address)  else c.REF_DEVICE_ID end)
   having n.id is not null
   ;

   commit;

insert into KIT_STATISTICS (kit_id,FASTEST_CAPTURE_TIME,slowest_capture_time,average_capture_time)
   select id, min_,max_,avg_ from PRE_KIT_STATISTICS;


update kit_statistics k set FASTEST_ACTIVITY = (
     select /*+parallel*/ listagg( activity,', '  ) within group (order by c.activity) as activity
   from ( select /*+parallel*/ distinct activity,
      (case when c.ref_device_id is null then upper(c.mac_address) else c.ref_device_id end) as device_id,
      duration from
   CLIENT_ACTIVITY_LOG c) c,  pre_kit_statistics e where
   e.device  =
   c.device_id
  and e.min_ = c.duration
   and e.id= k.kit_id
   );

commit;

   update kit_statistics k set slowest_ACTIVITY = (
     select /*+parallel*/ listagg( activity,', '  ) within group (order by c.activity) as activity
   from ( select /*+parallel*/ distinct activity,
      (case when c.ref_device_id is null then upper(c.mac_address) else c.ref_device_id end) as device_id,
      duration from
   CLIENT_ACTIVITY_LOG c) c,  pre_kit_statistics e where
   e.device  =
   c.device_id
  and e.max_ = c.duration
   and e.id= k.kit_id
   );

commit;

    update kit_statistics k set fastest_agent_name = (
     select  /*+parallel*/ listagg( full_name,', '  ) within group (order by c.full_name) as full_name
   from ( select /*+parallel*/ distinct full_name,
      (case when c.ref_device_id is null then upper(c.mac_address) else c.ref_device_id end) as device_id,
      duration from
   CLIENT_ACTIVITY_LOG c
   where c.ACTIVITY = 'END_TO_END_CAPTURE') c,  pre_kit_statistics e where
   e.device  =
   c.device_id
  and e.min_ = c.duration
   and e.id= k.kit_id
   );

   commit;

       update kit_statistics k set fastest_agent_email = (
     select /*+parallel*/ listagg( username,', '  ) within group (order by c.username) as username
   from ( select /*+parallel*/ distinct username,
      (case when c.ref_device_id is null then upper(c.mac_address) else c.ref_device_id end) as device_id,
      duration from
   CLIENT_ACTIVITY_LOG c
   where c.ACTIVITY = 'END_TO_END_CAPTURE') c,  pre_kit_statistics e where
   e.device  =
   c.device_id
  and e.min_ = c.duration
   and e.id= k.kit_id
   );

commit;

    update kit_statistics k set slowest_agent_name = (
     select /*+parallel*/ listagg( full_name,', '  ) within group (order by c.full_name) as full_name
   from ( select /*+parallel*/ distinct full_name,
      (case when c.ref_device_id is null then upper(c.mac_address) else c.ref_device_id end) as device_id,
      duration from
   CLIENT_ACTIVITY_LOG c
   where c.ACTIVITY = 'END_TO_END_CAPTURE') c,  pre_kit_statistics e where
   e.device  =
   c.device_id
  and e.max_ = c.duration
   and e.id= k.kit_id
   );

   commit;

       update kit_statistics k set slowest_agent_email = (
     select /*+parallel*/ listagg( username,', '  ) within group (order by c.username) as username
   from ( select /*+parallel*/ distinct username,
      (case when c.ref_device_id is null then upper(c.mac_address) else c.ref_device_id end) as device_id,
      duration from
   CLIENT_ACTIVITY_LOG c
   where c.ACTIVITY = 'END_TO_END_CAPTURE') c,  pre_kit_statistics e where
   e.device  =
   c.device_id
  and e.max_ = c.duration
   and e.id= k.kit_id
   );

   commit;

END KIT_STATISTICS_PCD$__sys__$;


