CREATE OR REPLACE PROCEDURE public.ncc_pcd2() AUTHID DEFINER
    IS $__sys__$ 
BEGIN

insert into  TEST_NCC4 
SELECT PHONE_NUMBER, UNIQUE_ID FROM biocapture.SMS_ACTIVATION_REQUEST Ss WHERE 
trunc(receipt_timestamp) >= trunc(sysdate)
and unique_id not in (select unique_id from ncc_record);

commit;

insert into TEMP_NCC_RECORD
select 
phone_number, receipt_timestamp AS kyc_date, s.unique_id,
(row_number() OVER (partition by  phone_number ORDER BY receipt_timestamp DESC)) AS p ,
b.id, '1010103' as cat_status
from biocapture.sms_activation_request s,biocapture.user_id u,biocapture.basic_data b
where S.UNIQUE_ID = u.unique_id
and B.USER_ID_FK = u.id
AND EXISTS(select 1 from TEST_NCC4 n where n.UNIQUE_ID = s.UNIQUE_ID);

commit;

insert into TEMP_NCC_RECORD_valid_new
select n.*, (row_number() OVER (partition by  unique_id ORDER BY unique_id DESC)) AS p3  from TEMP_NCC_RECORD n;

commit;

update  TEMP_NCC_RECORD_valid_new set cat_status = 0 where p3 > 1;

commit;

insert into ncc_record  (id,unique_id, phone_number,cat_status,basic_data_id,nr_part_key,bucket_status,SYNC_TIME )
select hibernate_sequence.nextval, unique_id, phone_number, cat_status, id, sysdate as date_ ,0,KYC_DATE from TEMP_NCC_RECORD_valid_new;

commit;
END NCC_PCD2$__sys__$;


ALTER PROCEDURE public.ncc_pcd2() OWNER TO seamfix;

