--

CREATE OR REPLACE PROCEDURE public.pilot_phase_devices_report_pcd() AUTHID DEFINER
    IS $__sys__$ 

BEGIN

truncate pilot_phase_devices_report;

commit;

insert into pilot_phase_devices_report
	with t1 as (
 select distinct	 S.phone_number,s.enrollment_ref as kit_tag, 
 	S.registration_timestamp,
 	round((extract(epoch from bb.FILE_SYNC_DATE)::numeric - extract(epoch from s.registration_timestamp)::numeric)/60, 2 ) capture2sync_mins,
 	round((extract(epoch from s.Receipt_Timestamp)::numeric  - extract(epoch from bb.File_Sync_Date)::numeric)/60, 2 ) sync2backend_mins,
 	round((extract(epoch from s.activation_timestamp)::numeric - extract(epoch from  s.Receipt_Timestamp)::numeric)/60, 2 ) backend2activation_mins,
		round((extract(epoch from s.activation_timestamp)::numeric - extract(epoch from  s.registration_timestamp)::numeric)/60, 2 )registration2activation_mins,
 	case 
 		when s.confirmation_status::text = '1' then 'PUSHED'
 		when s.confirmation_status::text = '0' then 'PENDING'
 	else s.confirmation_status::text end as mcentric_status, rank() over (partition by phone_number order by activation_date desc)
 from
 	meta_data m
 join basic_data b on
 	m.basic_data_fk = b.id
 join user_id u on
 	b.user_id_fk = u.id
 join sms_activation_request s on
 	s.unique_id = u.unique_id
 join bfp_sync_log bb on
 	s.unique_id = bb.unique_id
 where bfpsyncstatusenum = 'SUCCESS'
 and instance_ip = '10.152.89.174'
 and app_version in ('1.20', '1.3', '1.21', '1.31')
	)
select phone_number, registration_timestamp, 
 	 capture2sync_mins,
 	sync2backend_mins,
 	 backend2activation_mins, registration2activation_mins, 
  	 mcentric_status, kit_tag
from t1
where rank = 1 ;

		

commit;

END pilot_phase_devices_report_pcd$__sys__$;


