CREATE OR REPLACE PROCEDURE public.sim_trend() AUTHID DEFINER
    IS $__sys__$ 
BEGIN

execute immediate 'truncate table simrop_registration_trend_1';

COMMIT;

INSERT INTO simrop_registration_trend_1
Select N.Id As Node_Id,e.device_id, M.Capture_Machine_Id As Mac_Address, Trunc(s.Receipt_Timestamp) As Sync_Date, S.Enrollment_Ref, Count(S.Id) As Total_Syncs ,
count(distinct s.unique_id) as total_subscribers, sum(case when s.confirmation_status = 1 then 1 else 0 end) as total_activated,
Sum(Case When Registration_Type  In ('FIRST REGISTRATION','NMI','NMC','NSI','NSC', 'NMSI','NMSC') Then 1 Else 0 End) As New_Reg,
sum(case when Registration_Type  not In ('FIRST REGISTRATION','NMI','NMC','NSI','NSC','NMSI','NMSC') then 1 else 0 end) As Re_Reg,
Sum(Case When M.Within_Geofence = 1 Then 1 Else 0 End) As Inbound,
----sum(case when m.within_geofence = 0 then 1 else 0 end) as outbound
sum(case when m.within_geofence = 1 then 0 else 1 end) as outbound
from  sms_activation_request s left join user_id u on s.unique_id = u.unique_id
left join basic_data b on u.id = b.user_id_fk 
Left Join Meta_Data M On M.Basic_Data_Fk = B.Id
Left Join Node N On (Case When To_Char(M.Enrollment_Ref_Fk) Is Null Then Replace(Lower (M.Capture_Machine_Id),'-',':') Else To_Char(M.Enrollment_Ref_Fk) End) =
(case when to_char(n.enrollment_ref) is null then Replace(Lower (N.Mac_Address),'-',':') else to_char(n.enrollment_ref) end)
left join enrollment_ref e
 on n.enrollment_ref = e.id 
where TRUNC(RECEIPT_TIMESTAMP) = TRUNC(SYSDATE)
group by  m.capture_machine_id , trunc(s.receipt_timestamp), n.id, e.device_id, s.enrollment_ref;

COMMIT;

DELETE FROM simrop_registration_trend WHERE Sync_Date = TRUNC(SYSDATE);

insert into simrop_registration_trend
select * from simrop_registration_trend_1;

commit;

END SIM_TREND$__sys__$;


