--

CREATE OR REPLACE PROCEDURE public.tat_yearly() AUTHID DEFINER
    IS $__sys__$ 
BEGIN

insert into YEALY_REG  
SELECT
    DISTINCT S.phone_number,
    S.unique_id,
    S.registration_timestamp,
    S.receipt_timestamp,
    S.agility_push_timestamp as confirmation_timestamp,
    S.REGISTRATION_TYPE,
    bb.FILE_SYNC_DATE,
    ss.name as reg_state,
    BB.target_path,
	(CASE WHEN registration_network_status = 'OFF' then 'OFFLINE' else 'ONLINE' END) AS NETWORK_STATUS,
    (CASE WHEN DA14 = 'UNVERIFIED' then 'OFFLINE' else 'ONLINE' END) AS STATUS
from sms_activation_request s, bid_view b, meta_data m, bfp_sync_log bb, DYNAMIC_DATA D,state ss
where s.unique_id = bb.unique_id
AND s.phone_number=bB.msisdn
and bb.unique_id = b.unique_id
And B.Id = M.Basic_Data_Fk
And D.Basic_Data_Fk = B.Id
and s.state_id=ss.id
and bb.FILE_SYNC_DATE is not null
and bb.bfpsyncstatusenum = 'SUCCESS'
and bb.deleted='false'
and trunc(s.receipt_timestamp)=trunc(sysdate)-1;

commit;

insert  into yealy_reg_
 select Y.*,(DATE_PART('day', Confirmation_timestamp ::timestamp - FILE_SYNC_DATE::timestamp) * 24 + 
               DATE_PART('hour', Confirmation_timestamp ::timestamp - FILE_SYNC_DATE::timestamp)) * 60 +
               DATE_PART('minute', Confirmation_timestamp ::timestamp - FILE_SYNC_DATE::timestamp) AS SYNC2MCENTRIC,
			   (DATE_PART('day', FILE_SYNC_DATE ::timestamp - REGISTRATION_TIMESTAMP::timestamp) * 24 + 
               DATE_PART('hour', FILE_SYNC_DATE ::timestamp - REGISTRATION_TIMESTAMP::timestamp)) * 60 +
               DATE_PART('minute', FILE_SYNC_DATE ::timestamp - REGISTRATION_TIMESTAMP::timestamp) AS CAPTURE2SYNC,
			   (DATE_PART('day', Receipt_timestamp  ::timestamp - FILE_SYNC_DATE::timestamp) * 24 + 
               DATE_PART('hour', Receipt_timestamp ::timestamp - FILE_SYNC_DATE::timestamp)) * 60 +
               DATE_PART('minute', Receipt_timestamp ::timestamp - FILE_SYNC_DATE::timestamp) AS SYNC2BACKEND,
			   (DATE_PART('day', Confirmation_timestamp  ::timestamp - Receipt_timestamp::timestamp) * 24 + 
               DATE_PART('hour', Confirmation_timestamp ::timestamp - Receipt_timestamp::timestamp)) * 60 +
               DATE_PART('minute', Confirmation_timestamp ::timestamp - Receipt_timestamp::timestamp) AS BACKEND2MCENTRIC,
			   (DATE_PART('day', Confirmation_timestamp ::timestamp - REGISTRATION_TIMESTAMP::timestamp) * 24 + 
               DATE_PART('hour', Confirmation_timestamp ::timestamp - REGISTRATION_TIMESTAMP::timestamp)) * 60 +
               DATE_PART('minute', Confirmation_timestamp ::timestamp - REGISTRATION_TIMESTAMP::timestamp) AS CAPTURE2MCENTRIC,
			   (DATE_PART('day', Receipt_timestamp ::timestamp - REGISTRATION_TIMESTAMP::timestamp) * 24 + 
               DATE_PART('hour', Receipt_timestamp ::timestamp - REGISTRATION_TIMESTAMP::timestamp)) * 60 +
               DATE_PART('minute', Receipt_timestamp ::timestamp - REGISTRATION_TIMESTAMP::timestamp) AS CAPTURE2BACKEND
			   from yealy_reg Y 
               where trunc(receipt_timestamp)=trunc(sysdate)-1;

commit;

END TAT_YEARLY$__sys__$;


