CREATE OR REPLACE PROCEDURE public.update_node_sync_time() AUTHID DEFINER
    IS $__sys__$
BEGIN

execute immediate 'truncate table node_upd';

insert into node_upd
Select N.Id As Node_Id, max(s.Receipt_Timestamp) As Sync_Date
from sms_activation_request s left join user_id u on s.unique_id = u.unique_id
left join basic_data b on u.id = b.user_id_fk
left join meta_data m on M.Basic_Data_Fk = B.Id
left join node n on M.Enrollment_Ref_Fk = n.enrollment_ref
left join enrollment_ref e
on n.enrollment_ref = e.id
where TRUNC(RECEIPT_TIMESTAMP) = TRUNC(SYSDATE)
group by n.id;

UPDATE node d SET d.last_sync_time = (select T.sync_date from node_upd t where t.node_id = d.id)
where exists (select 1 from node_upd t where t.node_id = d.id);

commit;

End update_node_sync_time$__sys__$;


