CREATE VIEW public.device_mapper_ AS
 SELECT DISTINCT e.code,
    e.mac_address,
    k.name AS dealer,
    k.deal_code AS dealer_code,
    k.pk AS dealer_fk,
    k.orbita_id,
    d.name AS dept,
    dd.name AS dealer_division,
    k.email_address AS dealer_email,
    k.mobile_number AS dealer_mobile,
    k.km_user_pk,
    n.id AS node_id,
    k.zone_fk,
    zone.name AS region,
    state.name AS state,
    lga.name AS lga,
    area.name AS area,
    territory.name AS territory,
    outlet.name AS outlet,
    e.device_id,
    e.id AS enrollment_ref_id
   FROM (((((((((((public.node_bkup n
     JOIN public.node_assignment ns ON ((ns.node_fk = n.id)))
     JOIN public.enrollment_ref_2b__ e ON ((n.enrollment_ref = e.id)))
     JOIN public.kyc_dealer k ON ((ns.kyc_dealer_fk = k.pk)))
     JOIN public.dealer_type d ON ((k.dealer_type_fk = d.pk)))
     LEFT JOIN public.dealer_division dd ON ((k.dealer_division_fk = dd.pk)))
     LEFT JOIN public.area ON ((ns.area_fk = area.id)))
     LEFT JOIN public.lga ON ((area.lga_fk = lga.id)))
     LEFT JOIN public.state ON ((lga.state_fk = state.id)))
     LEFT JOIN public.zone ON ((state.zone_fk = zone.id)))
     LEFT JOIN public.outlet ON ((ns.outlet_fk = outlet.pk)))
     LEFT JOIN public.territory ON ((ns.territory_fk = territory.id)));


