--
-- Name: device_reg_details; Type: VIEW; Schema: public; Owner: seamfix
--

CREATE VIEW public.device_reg_details WITH (security_barrier='false') AS
 WITH t1 AS (
         SELECT s.enrollment_ref,
            to_char(s.receipt_timestamp, 'YYYY-mm'::character varying) AS date_,
            s.phone_number,
            count(s.phone_number) AS r_count
           FROM public.sms_activation_request s
          GROUP BY s.enrollment_ref, (to_char(s.receipt_timestamp, 'YYYY-mm'::character varying)), s.phone_number
          ORDER BY s.enrollment_ref, (to_char(s.receipt_timestamp, 'YYYY-mm'::character varying))
        ), t2 AS (
         SELECT count(sms_activation_request.phone_number) AS d_count,
            to_char(sms_activation_request.receipt_timestamp, 'YYYY-mm'::character varying) AS date_,
            sms_activation_request.phone_number,
            sms_activation_request.enrollment_ref
           FROM public.sms_activation_request
          GROUP BY (to_char(sms_activation_request.receipt_timestamp, 'YYYY-mm'::character varying)), sms_activation_request.phone_number, sms_activation_request.enrollment_ref
         HAVING (count(*) > 1)
          ORDER BY (to_char(sms_activation_request.receipt_timestamp, 'YYYY-mm'::character varying)), sms_activation_request.enrollment_ref
        )
 SELECT t1.date_,
    t1.enrollment_ref AS kit_tag,
    sum(t1.r_count) AS r_count,
    COALESCE(sum(t2.d_count), (0)::numeric) AS d_count
   FROM (t1
     LEFT JOIN t2 ON ((((t1.enrollment_ref)::text = (t2.enrollment_ref)::text) AND ((t1.phone_number)::text = (t2.phone_number)::text))))
  GROUP BY t1.date_, t1.enrollment_ref
  ORDER BY t1.enrollment_ref, t1.date_;


