--

CREATE VIEW public.device_summary_view AS
 SELECT DISTINCT e.code AS tag,
    e.device_id,
    hs.longitude,
    hs.latitude,
    kd.name AS dealer_name,
    kd.deal_code AS dealer_code,
    dt.name AS dealer_type,
    dd.name AS dealer_division,
    n.black_listed AS blacklisted,
    hs.receipt_date,
    (((kmu.first_name)::text || ' '::text) || (kmu.surname)::text) AS assigned_agent,
    hs.coordinate_address,
    ds.name AS device_status,
    hs.network_strength,
    hs.rooted AS device_root_status,
    hs.location_accuracy,
    hs.os_name,
    hs.os_version,
    hs.processor_speed,
    hs.used_storage AS storage_used,
    hs.available_storage AS storage_available,
    hs.total_storage,
    hs.ram_size,
    hs.agent_name AS last_logged_inuser,
    hs.last_modified AS last_heartbeat_time,
    COALESCE(upper((hs.mac_address)::text), upper((e.mac_address)::text)) AS mac_address,
    dd.pk AS division_pk,
    n.id AS node_id,
    kd.pk AS dealer_pk,
    lg.name AS lga,
    s.name AS state,
    re.name AS region,
    ar.name AS area,
    zo.name AS zone,
    lg.id AS lga_id,
    s.id AS state_id,
    re.id AS region_id,
    e.id AS enrollment_ref_id,
    e.installed_by,
    n.last_installed_update AS app_version,
    dtp.name AS device_type,
    n.last_sync_time,
    n.machine_model AS kit_model,
    hs.app_version AS geotracker_app_version,
    territory.name AS territory,
    oo.name AS assigned_outlet
   FROM ((((((((((((((((((public.heart_beat_status hs
     FULL JOIN public.enrollment_ref e ON (((
        CASE
            WHEN (e.device_id IS NULL) THEN e.mac_address
            ELSE e.device_id
        END)::text = (
        CASE
            WHEN (hs.ref_device_id IS NULL) THEN hs.mac_address
            ELSE hs.ref_device_id
        END)::text)))
     LEFT JOIN public.node n ON ((e.id = n.enrollment_ref)))
     LEFT JOIN public.node_assignment na ON ((n.id = na.node_fk)))
     LEFT JOIN public.area ar ON ((na.area_fk = ar.id)))
     LEFT JOIN public.lga lg ON ((ar.lga_fk = lg.id)))
     LEFT JOIN public.territory ON ((lg.territory_fk = territory.id)))
     LEFT JOIN public.state s ON ((lg.state_fk = s.id)))
     LEFT JOIN public.zone zo ON ((s.zone_fk = zo.id)))
     LEFT JOIN public.region re ON ((zo.region_fk = re.id)))
     LEFT JOIN public.km_user kmu ON ((na.fsa_user_fk = kmu.pk)))
     LEFT JOIN public.kyc_dealer kd ON ((na.kyc_dealer_fk = kd.pk)))
     LEFT JOIN public.dealer_type dt ON ((kd.dealer_type_fk = dt.pk)))
     LEFT JOIN public.dealer_division dd ON ((kd.dealer_division_fk = dd.pk)))
     LEFT JOIN public.device_status ds ON ((n.device_status_fk = ds.id)))
     LEFT JOIN public.device_type dtp ON ((n.device_type = dtp.id)))
     LEFT JOIN public.device_tag_request dtr ON (((dtr.kit_tag)::text = (e.code)::text)))
     LEFT JOIN public.device_tag_request_ext dtre ON ((dtre.pk = dtr.pk)))
     LEFT JOIN public.outlet oo ON ((oo.pk = dtre.outlet_fk)))
  WHERE (NOT (
        CASE
            WHEN (kd.pk IS NULL) THEN (1)::bigint
            ELSE kd.pk
        END IN ( SELECT dealer_exclusion_list.dealer_fk
           FROM public.dealer_exclusion_list)));


