--
-- Name: masterlist; Type: VIEW; Schema: public; Owner: seamfix
--

CREATE VIEW public.masterlist AS
 SELECT row_number() OVER (ORDER BY n.id) AS id,
    e.code AS tag,
    n.mac_address,
    nm.name AS dealer,
    ( SELECT ku.surname
           FROM public.km_user ku,
            public.km_role kr,
            public.user_role ur
          WHERE ((ur.role_fk = kr.pk) AND (ur.user_fk = ku.pk) AND (ku.pk = na.fsa_user_fk) AND ((kr.role)::text = 'FSA'::text))) AS fsa,
    ( SELECT ku.surname
           FROM public.km_user ku,
            public.km_role kr,
            public.user_role ur
          WHERE ((ur.role_fk = kr.pk) AND (ur.user_fk = ku.pk) AND (ku.pk = na.dm_user_fk) AND ((kr.role)::text = 'KDM'::text))) AS district_manager,
    z.name AS zone,
    s.name AS state,
    vt.name AS vendor_type,
    ds.name AS status,
    n.deployment_category,
    'SMART CLIENT'::text AS client_type,
    nt.type AS tag_type,
    n.machine_serial_number AS machine_serial,
    n.purchase_year,
    ( SELECT count(DISTINCT km_user.mobile) AS tot
           FROM public.km_user,
            public.km_role,
            public.user_role
          WHERE ((user_role.role_fk = km_role.pk) AND (user_role.user_fk = km_user.pk) AND (km_user.pk = na.fsa_user_fk) AND (upper((km_role.role)::text) = 'AGENT'::text) AND ((km_role.active)::smallint = 1))) AS number_of_agents,
    1 AS active,
    n.machine_model,
    n.last_sync_time,
    nm.deal_code,
    r.name AS region,
    n.last_installed_update,
    n.active AS kitappversion
   FROM ((((((((public.node_bkup n
     LEFT JOIN public.node_assignment na ON ((na.node_fk = n.id)))
     LEFT JOIN public.kyc_dealer nm ON ((na.kyc_dealer_fk = nm.pk)))
     LEFT JOIN public.device_status ds ON ((ds.id = n.device_status_fk)))
     LEFT JOIN public.state s ON ((na.state_fk = s.id)))
     LEFT JOIN public.zone z ON ((na.zone_fk = z.id)))
     LEFT JOIN public.region r ON ((r.id = s.region_fk)))
     LEFT JOIN public.dealer_type vt ON ((nm.dealer_type_fk = vt.pk)))
     LEFT JOIN public.kit_type nt ON ((nt.pk = n.kit_type_fk))),
    public.enrollment_ref_2b__ e
  WHERE ((n.mac_address)::text = (e.mac_address)::text);


